import React, { Component } from 'react';
import bottle                 from '../assets/bottle.svg';
import '../css/App.css';

import { establishments }    from './establishments/fixtures';
import Establishment         from './establishments/Establishment';

class App extends Component {

    constructor(props) {
          super(props);
          this.state = {
              pseudo  : "STRANGER",
              searchStringUser: "",
          }
          this.handleChange = this.handleChange.bind(this);
      }

      handleChange(e){
          this.setState({searchStringUser: e.target.value});
      }

      randomPseudo = () => {
          let randomPseudo    = ""
          const possible      = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz"
          const size          = Math.floor(Math.random() * 10) + 5

          for( let i=0; i < size; i++ ){
              randomPseudo += possible.charAt(Math.floor(Math.random() * possible.length))
          }
          this.setState({
              pseudo : randomPseudo
          })
      }

  render() {
    const listEstablishment = establishments.map( (establishment) => {
      return (
       <Establishment key={ establishment.id } establishment={ establishment }/>
        )
    })

    return (
        <div className="App">
            <header className="App-header">
            <img src={bottle} className="App-logo" alt="logo" />
            <h2>Welcome "{ this.state.pseudo }" to { this.props.title }</h2>
            </header>
            <div className="App-intro">
                <p> <button onClick={ this.randomPseudo } >Changer le pseudo !</button> </p>
            <div>
                    <input
                        type="text"
                        placeholder="search"
                        value={this.state.searchStringUser}
                        onChange={this.handleChange}
                    />
                </div>
                <section>
                    { listEstablishment }
                </section>
            </div>
        </div>
    );
}
}

export default App;
